using System.Threading.Tasks;\nusing KesifUygulamasiTemplate.Models;\n\nnamespace KesifUygulamasiTemplate.Services.Interfaces\n{\n    /// <summary>\n    /// Rota optimizasyon türleri\n    /// </summary>\n    public enum RouteOptimizationType\n    {\n        Fastest,    // Trafik + hız (varsayılan)\n        Shortest,   // Mesafe\n        Eco,        // Yakıt/enerji verimliliği\n        Safe        // Riskli yolları azalt\n    }\n\n    /// <summary>\n    /// Interface for route generation services\n    /// </summary>\n    public interface IRouteService\n    {\n        /// <summary>\n        /// Gets route points between two locations\n        /// </summary>\n        /// <param name="start">Starting location</param>\n        /// <param name="end">Ending location</param>\n        /// <param name="optimizationType">Route optimization type</param>\n        /// <returns>List of location points that make up the route</returns>\n        Task<System.Collections.Generic.List<LocationModel>> GetRouteAsync(LocationModel start, LocationModel end, RouteOptimizationType optimizationType = RouteOptimizationType.Fastest);\n\n        /// <summary>\n        /// Gets the current route provider name\n        /// </summary>\n        string CurrentProvider { get; }\n    }\n}\n