using System;\n\nnamespace KesifUygulamasiTemplate.Services {\n    public static class OfflineRouteSimulator {\n        public static TimeSpan EstimateDuration(double distanceKm, double averageSpeedKmh = 50) {\n            if (averageSpeedKmh <= 0 || distanceKm <= 0)\n                return TimeSpan.Zero;\n\n            double hours = distanceKm / averageSpeedKmh;\n            return TimeSpan.FromHours(hours);\n        }\n    }\n}\n