using System;\nusing System.Collections.Generic;\nusing Microsoft.Maui.Devices.Sensors;\n\nnamespace KesifUygulamasiTemplate.Services\n{\n    public class Route\n    {\n        public string RouteId { get; set; } = Guid.NewGuid().ToString();\n        public required Location Start { get; set; }\n        public required Location End { get; set; }\n        public List<Location> Path { get; set; } = new List<Location>();\n        public List<RouteStep> Steps { get; set; } = new List<RouteStep>();\n        public double DistanceKm { get; set; }\n        public TimeSpan Duration { get; set; }\n        public TransportMode TransportMode { get; set; }\n\n        // Eksik olan özellikler\n        public List<Location> Points { get; set; } = new List<Location>();\n        public double Distance { get; set; }\n\n        // Trafik için eklenen özellikler\n        public TimeSpan DurationInTraffic { get; set; }\n        public TrafficLevel TrafficLevel { get; set; }\n        public List<TrafficWarning> Warnings { get; set; } = new();\n    }\n\n    public class RouteStep\n    {\n        public required string Instruction { get; set; }\n        public required Location StartLocation { get; set; }\n        public required Location EndLocation { get; set; }\n        public double DistanceKm { get; set; }\n        public TimeSpan Duration { get; set; }\n        public required string ManeuverType { get; set; } // turn-right, turn-left, etc.\n    }\n\n    public enum TransportMode\n    {\n        Driving,\n        Walking,\n        Bicycling,\n        Transit\n    }\n}\n